var common       = require('../common');
var test         = require('utest');
var assert       = common.assert;
var File = common.require('file');

var file;
var now = new Date;
test('IncomingForm', {
  before: function() {
    file = new File({
      size: 1024,
      path: '/tmp/cat.img',
      name: 'cat.img',
      type: 'image/img',
      lastModifiedDate: now,
      filename: 'cat.img',
      mime: 'image/img'
    })
  },

  '#toJSON()': function() {
    var obj = file.toJSON();
    var len = Object.keys(obj).length;
    assert.equal(1024, obj.size);
    assert.equal('/tmp/cat.img', obj.path);
    assert.equal('cat.img', obj.name);
    assert.equal('image/img', obj.type);
    assert.equal('image/img', obj.mime);
    assert.equal('cat.img', obj.filename);
    assert.equal(now, obj.mtime);
    assert.equal(len, 8);
  }
});